<?php foreach ($patient_materials as $patient_material) {    $ext = pathinfo($patient_material->url, PATHINFO_EXTENSION);    ?>    <div class="panel col-md-5"  style="height: 200px; margin-right: 12px; margin-bottom: 36px; background: #f1f1f1; padding:5px;">        <div class="post-info">            <?php            $extensionImg = array("jpeg", "jpg", "png", "gif", "pdf" );            $extensionVideo = array(  "mp4", "wav", "ogg", "avi","webm");            if(in_array($ext,$extensionImg)){ ?>                <a href=<?php echo $patient_material->url; ?>" data-lightbox="example-1">                <img class="example-image" src="<?php echo $patient_material->url; ?>" alt="image-1" height="100" width="100"/></a>            <?php }            if(in_array($ext,$extensionVideo)){ ?>                <a class="example-image-link" href="<?=base_url($patient_material->url)?>"  data-lightbox="example-1">                    <video controls width="100%">                        <source src="<?=base_url($patient_material->url) ?>"                                type="video/webm">                        <source src="<?=base_url($patient_material->url) ?>"                                type="video/mp4">                        Sorry, your browser doesn't support embedded videos.                    </video>                </a>            <?php }?>            <!--  <img src="<?php echo $patient_material->url; ?>" height="100" width="100">-->        </div>        <div class="post-info">            <?php            if (!empty($patient_material->title)) {                echo $patient_material->title;            }            ?>        </div>        <p></p>        <div class="post-info">            <a class="btn btn-info btn-xs btn_width" href="<?php echo $patient_material->url; ?>" download> <?php echo lang('download'); ?> </a>            <?php if (!$this->ion_auth->in_group(array('Patient'))) { ?>                <a href="javascript:;" class="btn btn-info btn-xs btn_width docDeleteBtn" title="<?php echo lang('delete'); ?>" data-ref="patient/deletePatientMaterial?id=<?php echo $patient_material->id; ?>"onclick="return confirm('Are you sure you want to delete this item?');"> X </a>            <?php } ?>        </div>        <hr>    </div><?php } ?>